    def connecter(self):
        self.timer = QTimer()
        self.timer.timeout.connect(self.showtime)
        self.timer.start()
        self.get_cur_name.currentIndexChanged['QString'].connect(self.showmoneyname)
        self.exchange_inquiry.clicked.connect(self.inq)
        self.lineEdit.textChanged.connect(self.calculate_rate)
    def showtime(self):  # 显示时间
        try:
            datetime = QDateTime.currentDateTime()
            text = datetime.toString()
            self.timelabel.setText(text)
        except:
            self.timelabel.setText("无法获取时间")

    def showmoneyname(self, money):
        if money != '货币名称':
            self.display_bar2.setText(money_dict[money])
        else:
            self.display_bar2.clear()

    def inq(self):
        try:
            from_money = money_dict[self.from_cur.currentText()]
            to_money = money_dict[self.to_cur.currentText()]
            if from_money != '' and to_money != '':
                if from_money == to_money:
                    self.display_bar3.setText("1:1")
                else:
                    self.display_bar3.setText(cur_exchange('cd0ddf0d24e0291f18d0d519d04700af', from_money, to_money))

            else:
                self.display_bar3.clear()
        except Exception as e:
            self.display_bar3.setText("无法获取汇率信息")
        finally:
            self.calculate_rate()

    def calculate_rate(self):
        if self.lineEdit.text():
            list = self.display_bar3.text().split(":")
            if len(list) == 2 and list[0] == "1":
                rate = float(list[1])
                v = float(self.lineEdit.text())
                self.lineEdit_2.setText(str(round(v * rate, 6)))
            else:
                self.lineEdit_2.clear()

