    def calculator(self):
        try:
            if not (self.lineEdit_1.text() and self.lineEdit_2.text() and self.lineEdit_3.text()):
                QMessageBox.information(None, "错误", "请输入", QMessageBox.Ok)
                return
            n = float(self.lineEdit_1.text())
            a = float(self.lineEdit_2.text())*10000
            i = float(self.lineEdit_3.text())/100
            if (n<0 or a<0 or i<0) or (n==0 and a>0):
                QMessageBox.information(None, "错误", "输入不合法", QMessageBox.Ok)
                return
            B=Y=S=0.0
            if self.radioButton.isChecked() and i>0:                     # 等额本息计算
                B=a*i*(1+i)**n/((1+i)**n-1)
                Y=n*a*i*(1+i)**n/((1+i)**n-1)-a
                S=n*a*i*(1+i)**n/((1+i)**n-1)
            elif self.radioButton_2.isChecked() or (i==0 and n!=0):                  # 等额本金计算
                Y=(n+1)*a*i/2
                S=(n+1)*a*i/2+a
                B=S/n
            self.lineEdit_4.setText(str(round(B,2)))
            self.lineEdit_5.setText(str(round(Y,2)))
            self.lineEdit_6.setText(str(round(S,2)))
        except Exception as e:
            print(e)
        finally:
            return


