    def converse(self):
        text = self.lineEdit.text()
        if text == "":
            self.label_hex.clear()
            self.label_dec.clear()
            self.label_oct.clear()
            self.label_bin.clear()
        else:
            if self.radioButton.isChecked():
                self.label_hex.setText(text)
                self.label_dec.setText(str(eval("0x" + text)))
                self.label_oct.setText(str(oct(eval("0x" + text)))[2:])
                self.label_bin.setText(str(bin(eval("0x" + text)))[2:])
            elif self.radioButton_2.isChecked():
                self.label_hex.setText(str(hex(int(text)))[2:].upper())
                self.label_dec.setText(text)
                self.label_oct.setText(str(oct(int(text)))[2:])
                self.label_bin.setText(str(bin(int(text)))[2:])
            elif self.radioButton_3.isChecked():
                self.label_hex.setText(str(hex(eval("0o" + text)))[2:].upper())
                self.label_dec.setText(str(eval("0o" + text)))
                self.label_oct.setText(text)
                self.label_bin.setText(str(bin(eval("0o" + text)))[2:])
            else:
                self.label_hex.setText(str(hex(eval("0b" + text)))[2:].upper())
                self.label_dec.setText(str(eval("0b" + text)))
                self.label_oct.setText(str(oct(eval("0b" + text)))[2:])
                self.label_bin.setText(text)


